import React from 'react';
import styled, {css} from 'styled-components';
import toTop from './images/up_arrow.png';

const Wrapper = styled.div`
  padding: 1em; 
  background: blue;
`;

const Img = styled.img`
    display: block; 
    position: sticky;
    bottom: 4px;
    float: right;
    z-index: 99;
    cursor: pointer;
`;
// function topScroll(e){
//     let element = e.target
//     if(element.scrollHeight - element.scrollTop > element.clientHeight/3){
//         e.
//     }
// }

function toFunction(){
  document.body.scrollTop = 0; // for Safari
  document.documentElement.scrollTop = 0; //for Chrome, FF, IE, and Opera
}
const ToTop = (props) =>(
    <div>
 <p id={'showScroll'}>{window.addEventListener('scroll', function () {
                document.getElementById('showScroll').innerHTML = window.pageYOffset + 'px';
                if(window.pageYOffset === 7000){
                  document.getElementbyId('showScroll;').innerHTML="hello world";
                }
            })}</p>
   <Img src={toTop} onClick={toFunction}/>
</div>
)

export default ToTop;
