            elements.push(i.toString());
            inerElements.push(input[i].toString())
        }

    }
    function renderElements(p){
    for(let i in p){
        p.map(pro =>
        <Project title={pro.title} image={pro.image} href={pro.href}/>
    )
    }
}


      return (
              <Figure>
                  <p>server call: </p>
                  {mapElements(projects)}
                  <p>{projects.toString()}</p>
              </Figure>
      );
}



export default Gallery;
