import React from 'react';
import styled, {css} from 'styled-components';
import Paragraph from "./Paragraph";
const Wrapper = styled.section`
    margin: 5% 0 5% 0;
    padding: 0 5% 2% 5%;
    display: flex;
    flex-flow: row wrap;

`;

const Column = styled.div`
    padding: 0;
    width: 50%;
    height: 100%;
`;
const Title = styled.div`
    text-align: left;
    font-family: Times New Roman, serif;
    font-size: 30px;
    color: #000002;
    font-weight: lighter;
    margin: 0;
`;
var members, roles = [];

function getItems(data, roles, members){
    for(let d in data){
        if(d.role != null){
            roles.push(d);
        }else if(d.members != null){
            members.push(d);
        }
    }
}
const Team = (props) => (

    <Wrapper>
        { getItems(props.team, roles, members) }
        <Column>
            <Title>My Role</Title>
            {roles.map((r, index)=>{
                   return <p>{r}</p>
            })}

            <p>{members}</p>

            </Column>
        <Column>
            <Title>Team</Title>

        </Column>
    </Wrapper>
)

export default Team;
