import React from 'react';
import styled, {css} from 'styled-components';
import toTop from './images/ic_arrow_upward_black_48dp.png';



const Img = styled.img`
  display: none;
  position: sticky;
  bottom: 20px;
  right: -30px;
  z-index: 99;
  cursor: pointer;
  padding: 15px;
  height: 55px;
  opacity: 5;
}
`
function returnTop(){
    function back(){
  window.history.back();
}
// top btn
topBtn = document.getElementById("topBtn");

window.onscroll = function () {scrollFunction()};

function scrollFunction(){
  if(document.body.schoolTop > 200 || document.documentElement.scrollTop > 200){
    topBtn.style.display = "block";
  }else{
    topBtn.style.display = "none";
  }
}
}
function toFunction(){
  document.body.scrollTop = 0; // for Safari
  document.documentElement.scrollTop = 0; //for Chrome, FF, IE, and Opera
}
const ToTop = (props) =>(
    <Img scr={toTop} onClick={toFunction()}/>
)

export default ToTop;
