import React from "react";
import styled from "styled-components";
import WeirdImageGrid from "./WeirdImageGrid";
import Column_Thin from "./Column_Thin";

const Wrapper = styled.section`
    align-item: start; 
    margin: 5% 0 5% 0;
    padding 0 5% 2% 5%;
    display: flex;
    flex-direction: column;
@media only screen and (min-width: 992px){
    display: flex;
    flex-direction: row reverse;
    }
`;
const Underline = styled.section`
    border-bottom-color: #cbcbcb;
    border-bottom-width: 1px;
    border-bottom-style: solid;
`;
function getElements(e){
    if(e.thin_column){
    return <Column_Thin data={e.thin_column}/>
    }else if(e.weird_image_grid){
        return <WeirdImageGrid image={e.weird_image_grid}/>
    }
}

const SectionReverse=(props)=>(
    <Wrapper>
        <Underline>
        {props.section_underline_reverse.map((c, index)=>{
            return c.getElements
        })}
        </Underline>
    </Wrapper>
)

export default SectionReverse;
