import React, {Component, useEffect, useState} from 'react';
import styled, {css} from 'styled-components';
import Project from "./Project";

const Figure = styled.figure`
    margin: 0;
    padding: 0;
     display: flex;
    flex-flow: row wrap;

`;
const Row = styled.div`
    padding: 0;
    margin: 0;
    display: flex;
    flex-direction: column;
@media only screen and (min-width: 992px){
    display: flex;
    flex-direction: row;
    }
`;

function Gallery() {
    const [projects , setProjects] = useState([]);
    useEffect(()=> {
        fetch('/gallery').then(res => res.json()).then(data =>{
            setProjects(data.data)
     }).catch(function(err){
       console.log(err)
     })
   }, []);
    var payload = JSON.parse(
        '      {"prime":\n' +
        '         {"title": "Prime Day",\n' +
        '          "image": "./flask_api/static/images/prime_day//pdc_desktop_hero_image.png",\n' +
        '          "href": "#"}\n' +
        '         ,\n' +
        '        "oft":\n' +
        '        {"title": "OFT",\n' +
        '         "image": "./flask_apistatic/images/oprah_fav//oft_desktop_hero_image.png",\n' +
        '         "href": "#"}\n' +
        '         ,\n' +
        '        "flair":\n' +
        '        {"title": "Flair",\n' +
        '         "image": "./flask_apistatic/images/flair//flair_desktop_hero_image.png",\n' +
        '         "href": "#"}\n' +
        '         ,\n' +
        '        "amazon":\n' +
        '        {"title": "Amazon Pets",\n' +
        '         "image": "./flask_apistatic/images/pets//pets_desktop_hero_image.png",\n' +
        '         "href": "#"}\n' +
        '         }');
var p = JSON.parse('{ "name":"John", "age":30, "city":"New York"}');
    return(
              <Figure>

                      {projects.map((p, index)=>{
                          return <div><h1>{p.title}</h1> <h6>{p.image}</h6></div>
                  })}
              </Figure>
      );
}



export default Gallery;
