import React from 'react';
import styled, {css} from 'styled-components';
import toTop from './images/up_arrow.png';

const Img = styled.p`
    display: block; 
    position: sticky;
    bottom: 4px;
    float: right;
    z-index: 99;
    cursor: pointer;
`;
