import React, {Component, useEffect, useState} from 'react';
import styled, {css} from 'styled-components';
import Project from "./Project";

const Figure = styled.figure`
    margin: 0;
    padding: 0;
     display: flex;
    flex-flow: row wrap;

`;
const Row = styled.div`
    padding: 0;
    margin: 0;
    display: flex;
    flex-direction: column;
@media only screen and (min-width: 992px){
    display: flex;
    flex-direction: row;
    }
`;

function Gallery() {
    const [projects , setProjects] = useState([]);
    useEffect(()=> {
        fetch('/gallery').then(res => res.json()).then(data =>{
            setProjects(data.data)
     }).catch(function(err){
       console.log(err)
     })
   }, []);
    var elements = [];
    var inerElements = [];
    var jsonEllemnts = [];
    //var json = JSON.parse(projects);
    function mapElements(input){
        for(let i in input){
            elements.push(i.toString());
            inerElements.push(input[i].toString())
            for(let ii in i){
                jsonEllemnts.push(JSON.parse(ii));
            }
        }

    }
    function renderElements(p){
    for(let i in p){
        p.map(pro =>
        <Project title={pro.title} image={pro.image} href={pro.href}/>
    )
    }
}


      return (
              <Figure>
                  <p>server call: </p>
                  {mapElements(projects)}
                  <br/>
                  <p>project toString: </p>
                  <p>{projects.toString()}</p>
                  <br/>
                  <p>inerElement: </p>
                  <p>{inerElements}</p>
                  <br/>
                  <p>projects amazon:  </p>
                  <p>{projects['prime']}</p>
                  {JSON.parse(findAll(projects))}
              </Figure>
      );
}



export default Gallery;
