import React, {Component, useEffect, useState} from 'react';
import styled, {css} from 'styled-components';
import Project from "./Project";

const Figure = styled.figure`
    margin: 0;
    padding: 0;
     display: flex;
    flex-flow: row wrap;

`;
const Row = styled.div`
    padding: 0;
    margin: 0;
    display: flex;
    flex-direction: column;
@media only screen and (min-width: 992px){
    display: flex;
    flex-direction: row;
    }
`;
function renderElements(p){
    for(let i in p){
        p.map(pro =>
        Project(pro.title, pro.image, pro.href)
    )
    }
}

function Gallery() {
    const [projects , setProjects] = useState([]);
    useEffect(()=> {
        fetch('/gallery').then(res => res.json()).then(data =>{
            setProjects(data.data)
     }).catch(function(err){
       console.log(err)
     })
   }, []);

    function mapElements(input){
        input.map((input)=> input * 2);
        return input
    }

      return (
              <Figure>
                  <p>server call: </p>
                  <div >{mapElements(projects)}</div>
              </Figure>
      );
}



export default Gallery;
